CREATE TABLE IF NOT EXISTS interviews (id INT AUTO_INCREMENT PRIMARY KEY, title VARCHAR(255) NOT NULL, description TEXT NULL, manager_id INT NOT NULL, talent_id INT NOT NULL, job_id INT NULL, proposal_id INT NULL, conversation_id INT NULL, status ENUM('created', 'sent', 'in_progress', 'completed', 'reviewed', 'next_steps', 'rejected', 'inappropriate', 'hold', 'cancelled') DEFAULT 'created', priority ENUM('low', 'medium', 'high', 'urgent') DEFAULT 'medium', questions JSON NULL, estimated_duration INT NULL, actual_duration INT NULL, scheduled_at TIMESTAMP NULL, started_at TIMESTAMP NULL, completed_at TIMESTAMP NULL, manager_rating INT NULL, manager_feedback TEXT NULL, talent_rating INT NULL, talent_feedback TEXT NULL, is_flagged BOOLEAN DEFAULT FALSE, flagged_reason TEXT NULL, flagged_by INT NULL, flagged_at TIMESTAMP NULL, admin_notes TEXT NULL, created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP, updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, deleted_at TIMESTAMP NULL);
